CREATE TABLE IF NOT EXISTS `hd_admin` (
  `id` int(11) NOT NULL,
  `name` varchar(32) NOT NULL COMMENT '昵称',
  `email` varchar(32) NOT NULL COMMENT '邮箱',
  `sex` varchar(11) NOT NULL COMMENT '性别',
  `number` int(32) NOT NULL COMMENT '手机号码',
  `password` varchar(32) NOT NULL COMMENT '密码',
  `mingzi` int(11) NOT NULL COMMENT '名字',
  `is_public` int(11) NOT NULL DEFAULT '1' COMMENT '性别是否公开，默认1公开',
  `account` varchar(32) NOT NULL COMMENT '账户',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

