--
-- 表的结构 `hd_comment`
--

CREATE TABLE IF NOT EXISTS `hd_comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `content` varchar(255) NOT NULL,
  `create_at` date NOT NULL,
  `email` varchar(32) NOT NULL,
  `isturn` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 AUTO_INCREMENT=1 ;

-- --------------------------------------------------------

--
-- 表的结构 `hd_users`
--

CREATE TABLE IF NOT EXISTS `hd_users` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(32) NOT NULL,
  `password` varchar(255) NOT NULL,
  `lock` tinyint(11) NOT NULL DEFAULT '0',
  `create_at` datetime NOT NULL,
  `logintime` timestamp NOT NULL,
  `loginip` varchar(32) NOT NULL,
  `admin` int(11) NOT NULL DEFAULT '1' COMMENT '0：超级管理员，1：管理员',
  PRIMARY KEY (`user_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8mb4 AUTO_INCREMENT=7 ;

